import requestsfrom random import  randintinitial_value_of_dollars = 0def get_money_interval(difficult,guess):    url = 'https://api.exchangerate.host/latest?base=USD'    response = requests.get(url)    data = response.json()    rate = float(data['rates'].get('ILS'))    print ("Real value is {} ".format(rate * initial_value_of_dollars))    if guess - (5-difficult) < rate * initial_value_of_dollars < guess + (5-difficult):        return True    else:        return Falsedef get_guess_from_user():    guess = int(input("Type in value in ILS for {} ".format(initial_value_of_dollars)))    return guessdef currency_play():    difficult = int(input("Type in difficulty "))    ils_guess = get_guess_from_user()    guess_try = get_money_interval(difficult,ils_guess)    if guess_try:        print ("You win")    else:        print ("You Lost")def currency_play_diff(difficult):    initial_value_of_dollars = randint(0, 100)    ils_guess = get_guess_from_user()    guess_try = get_money_interval(difficult,ils_guess)    if guess_try:        print ("You win")    else:        print ("You Lost")if __name__ == '__main__':    initial_value_of_dollars = randint(0,100)    currency_play()