from random import randintdef generate_number(diff):    return randint(0,diff)def get_guess_from_user(diff):    guess = int(input("Type in your guess from 0 to {} ".format(diff)))    if 0 < guess < diff:        return guess    else:        return -1def compare_results(guess,sec):    if guess == sec:        print("Won. The random number is {} ".format(sec))        return True    else:        print("Lost. The random number is {} and guess is {} ".format(sec,guess))        return  Falsedef guess_play():    difficult = int(input("Type in your difficulty "))    secret_number = generate_number(difficult)    # while get_guess_from_user(difficult) < 0:    guess = get_guess_from_user(difficult)    while (guess < 0):        guess = get_guess_from_user(difficult)    if compare_results(guess,secret_number):        return True    else:        return Falsedef guess_play_diff(difficult):    secret_number = generate_number(difficult)    # while get_guess_from_user(difficult) < 0:    guess = get_guess_from_user(difficult)    while (guess < 0):        guess = get_guess_from_user(difficult)    if compare_results(guess,secret_number):        return True    else:        return False# Press the green button in the gutter to run the script.if __name__ == '__main__':    guess_play()