import timefrom random import  randintimport pygamefrom disp import text_displaydef get_list_from_user(diff):    temp_user_list=list()    for i in range(diff):        guess = int(input("Type in your guess from 0 to 101 "))        while (guess < 0 or guess > 101):            guess = int(input("Type in your guess from 0 to 101 "))        temp_user_list.append(guess)    return temp_user_listdef generate_sequence(difficult):    temp_list=list()    for i in range(difficult):        temp_list.append(randint(0,101))    return temp_listdef is_list_equal(a,b):    if a == b:        return  True    else:        return Falsedef display_text(text):    pygame.init()    screen = pygame.display.set_mode([500, 500])    screen.fill((255, 255, 255))    pygame.display.set_caption('Show Text')    font = pygame.font.Font('freesansbold.ttf', 32)    textsurface = font.render(text, True, (100, 100, 100))    milliseconds = int(round(time.time() * 1000))    while int(round(time.time() * 1000)) - milliseconds < 7000:        screen.blit(textsurface, (100, 100))        # Flip the display        pygame.display.flip()    pygame.quit()def mem_play():    difficult = int(input("Type in your difficulty "))    list_of_randoms = generate_sequence(difficult)    text_display(str(list_of_randoms))    list_of_user_guess = get_list_from_user(difficult)    if is_list_equal(list_of_randoms,list_of_user_guess):        print("WIN list of numbers is {} ".format(list_of_randoms))        return True    else:        print("LOST list of numbers is {} original is {} ".format(list_of_user_guess,list_of_randoms))        return Falsedef mem_play_diff(difficult):    list_of_randoms = generate_sequence(difficult)    text_display(str(list_of_randoms))    list_of_user_guess = get_list_from_user(difficult)    if is_list_equal(list_of_randoms,list_of_user_guess):        print("WIN list of numbers is {} ".format(list_of_randoms))        return True    else:        print("LOST list of numbers is {} original is {} ".format(list_of_user_guess,list_of_randoms))        return False# Press the green button in the gutter to run the script.if __name__ == '__main__':    mem_play()